import { useState } from 'react';

import Col from 'react-bootstrap/esm/Col';
import Row from 'react-bootstrap/esm/Row';
import classes from './sections/Experiences.module.css';

const Experience = ({ exp }) => {
    const showText = "Show more +";
    const hideText = "Show less -";
    const [toggleAcc, setToggleAcc] = useState(false);
    const [toggleText, setToggleText] = useState(showText);

    const toggleAccHandler = () => {
        setToggleAcc(!toggleAcc);
        setToggleText(toggleAcc ? showText : hideText);
    }

    return ( 
        <div className={classes.experience}>
            <Row>
                <Col lg={9}>
                    <p className={classes.experiences__title}>{ exp.title }</p>
                </Col>
                <Col lg={3}>
                    <p className={classes.experiences__date}>{ exp.startDate } - { exp.endDate }</p>
                </Col>
            </Row>
            <Col>
                <div className={classes["experiences__company-wrapper"]}>
                    <img className={classes["experiences__company-logo"]} src={ exp.imgPath } alt="Publicis Sapient logo" />
                    <p className={classes.experiences__company}>{ exp.company }</p>
                </div>
            </Col>
            {toggleAcc &&
            <Col lg={8}>
                <ul className={classes.accomplishments}>
                    {exp.accomplishments.map((acc, i) => {
                        return <li key={i} className={classes.accomplishment}>{acc}</li>
                    })}
                </ul>
            </Col> }
            <Col>
                <button className={classes.accomplishment__btn} onClick={toggleAccHandler}>{toggleText}</button>
            </Col> 

        </div>
     );
}

export default Experience;
